// src/components/CheckoutPage.jsx
import { Button, Card, Table } from 'react-bootstrap';
import { CATALOG } from '../data/catalog';

export default function CheckoutPage({ cart, onBack, onConfirm }) {
  const detailedItems = cart
    .map((item) => {
      const product = CATALOG.find((p) => p.id === item.id);
      if (!product) return null;
      return {
        ...product,
        qty: item.qty,
        subtotal: product.price * item.qty,
      };
    })
    .filter(Boolean);

  const total = detailedItems.reduce((sum, item) => sum + item.subtotal, 0);
  const hasItems = detailedItems.length > 0;

  // ID de boleta simulado
  const orderId = `HH-${new Date().getTime().toString().slice(-6)}`;

  return (
    <div className="d-flex justify-content-center mt-4">
      <Card style={{ maxWidth: '700px', width: '100%' }} className="shadow-sm">
        <Card.Body>
          <Card.Title className="mb-3">ðŸ§¾ Boleta de compra</Card.Title>

          {!hasItems && (
            <p className="text-muted">
              No hay productos en el carrito. Vuelve al catÃ¡logo para agregar productos.
            </p>
          )}

          {hasItems && (
            <>
              <p className="small text-muted">
                NÂ° de boleta: <strong>{orderId}</strong>
              </p>

              <Table bordered size="sm" className="mb-3">
                <thead>
                  <tr>
                    <th>Producto</th>
                    <th>Cantidad</th>
                    <th>Precio</th>
                    <th>Subtotal</th>
                  </tr>
                </thead>
                <tbody>
                  {detailedItems.map((item) => (
                    <tr key={item.id}>
                      <td>{item.name}</td>
                      <td>{item.qty}</td>
                      <td>${item.price.toLocaleString('es-CL')}</td>
                      <td>${item.subtotal.toLocaleString('es-CL')}</td>
                    </tr>
                  ))}
                </tbody>
                <tfoot>
                  <tr>
                    <td colSpan={3} className="text-end fw-bold">
                      Total
                    </td>
                    <td className="fw-bold">
                      ${total.toLocaleString('es-CL')}
                    </td>
                  </tr>
                </tfoot>
              </Table>

              <p className="small text-muted">
                Gracias por comprar en <strong>Huerto Hogar</strong>. Esta es una boleta
                simulada para fines de evaluaciÃ³n.
              </p>
            </>
          )}

          <div className="d-flex justify-content-between mt-3">
            <Button variant="outline-secondary" onClick={onBack}>
              â¬… Volver al catÃ¡logo
            </Button>

            <Button
              variant="success"
              disabled={!hasItems}
              onClick={() => onConfirm(orderId)}
            >
              âœ… Confirmar compra
            </Button>
          </div>
        </Card.Body>
      </Card>
    </div>
  );
}
