// src/App.js
import { useEffect, useState } from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import { Routes, Route, useNavigate } from 'react-router-dom';
import ProductList from './components/ProductList';
import ShoppingCart from './components/ShoppingCart';
import CheckoutPage from './components/CheckoutPage';

const CART_KEY = 'hh_cart_v1';

function loadCart() {
  try {
    const raw = window.localStorage.getItem(CART_KEY);
    return raw ? JSON.parse(raw) : [];
  } catch {
    return [];
  }
}

function saveCart(cart) {
  try {
    window.localStorage.setItem(CART_KEY, JSON.stringify(cart));
  } catch {
    // ignorar errores de localStorage
  }
}

export default function App() {
  const navigate = useNavigate();

  const [cart, setCart] = useState(() => loadCart());

  useEffect(() => {
    saveCart(cart);
  }, [cart]);

  const cartCount = cart.reduce((sum, item) => sum + item.qty, 0);

  const handleAddToCart = (id) => {
    setCart((prev) => {
      const index = prev.findIndex((item) => item.id === id);
      if (index >= 0) {
        const updated = [...prev];
        updated[index] = {
          ...updated[index],
          qty: updated[index].qty + 1,
        };
        return updated;
      }
      return [...prev, { id, qty: 1 }];
    });
  };

  const handleRemoveFromCart = (id) => {
    setCart((prev) => prev.filter((item) => item.id !== id));
  };

  const handleClearCart = () => {
    setCart([]);
  };

  const handleUpdateQty = (id, newQty) => {
    setCart((prev) =>
      prev.map((item) =>
        item.id === id
          ? { ...item, qty: Math.max(1, newQty) }
          : item
      )
    );
  };

  // ðŸ‘‰ ir a /checkout
  const goToCheckout = () => {
    navigate('/checkout');
  };

  // cuando confirma compra en /checkout
  const handleConfirmPurchase = (orderId) => {
    alert(`Compra confirmada.\nBoleta: ${orderId}`);
    setCart([]);      // limpiar carrito
    navigate('/');    // volver al catÃ¡logo
  };

  const goBackToCatalog = () => {
    navigate('/');
  };

  return (
    <Container className="mt-4">
      {/* Header con badge del carrito */}
      <div className="d-flex justify-content-between align-items-center mb-4">
        <div className="text-center flex-grow-1">
          <h1>Huerto Hogar (React)</h1>
          <p className="text-muted mb-0">
            Productos frescos directo del campo
          </p>
        </div>
        <div className="ms-3 text-end">
          <div className="mb-1">ðŸ›’ Carrito</div>
          <span className="badge bg-success fs-6">{cartCount}</span>
        </div>
      </div>

      <Routes>
        {/* Ruta principal: catÃ¡logo + carrito */}
        <Route
          path="/"
          element={
            <Row>
              <Col md={8} lg={9}>
                <ProductList onAddToCart={handleAddToCart} />
              </Col>
              <Col md={4} lg={3}>
                <ShoppingCart
                  cart={cart}
                  onRemove={handleRemoveFromCart}
                  onClear={handleClearCart}
                  onUpdateQty={handleUpdateQty}
                  onCheckout={goToCheckout}   // ðŸ‘ˆ importante
                />
              </Col>
            </Row>
          }
        />

        {/* Ruta de checkout (boleta) */}
        <Route
          path="/checkout"
          element={
            <CheckoutPage
              cart={cart}
              onBack={goBackToCatalog}
              onConfirm={handleConfirmPurchase}
            />
          }
        />
      </Routes>
    </Container>
  );
}
