// src/components/ShoppingCart.jsx
import { Card, ListGroup, Button, Image } from 'react-bootstrap';

export default function ShoppingCart({
  cart,
  onRemove,
  onClear,
  onUpdateQty,
  onCheckout,
}) {
  const detailedItems = cart.map((item) => ({
    ...item,
    subtotal: (item.price || 0) * item.qty,
  }));

  const total = detailedItems.reduce((sum, item) => sum + item.subtotal, 0);
  const hasItems = detailedItems.length > 0;

  return (
    <Card className="shadow-sm position-sticky" style={{ top: '1rem' }}>
      <Card.Body>
        <Card.Title>ðŸ›’ Carrito</Card.Title>

        {!hasItems && (
          <p className="text-muted mb-3">Tu carrito estÃ¡ vacÃ­o.</p>
        )}

        {hasItems && (
          <ListGroup className="mb-3">
            {detailedItems.map((item) => (
              <ListGroup.Item key={item.key}>
                <div className="d-flex justify-content-between align-items-start gap-3">
                  <div className="d-flex gap-3">
                    {item.img && (
                      <Image
                        src={item.img}
                        alt={item.name}
                        rounded
                        style={{ width: 56, height: 56, objectFit: 'cover' }}
                      />
                    )}
                    <div>
                      <strong>{item.name}</strong>
                      <div className="small text-muted">
                        {item.unit} Â· ${item.price.toLocaleString('es-CL')}
                      </div>
                      <div className="small text-muted">
                        Subtotal: ${item.subtotal.toLocaleString('es-CL')}
                      </div>
                      <div className="d-flex align-items-center gap-2 mt-2">
                        <Button
                          variant="outline-secondary"
                          size="sm"
                          onClick={() => onUpdateQty(item.key, item.qty - 1)}
                        >
                          âˆ’
                        </Button>
                        <span>{item.qty}</span>
                        <Button
                          variant="outline-secondary"
                          size="sm"
                          onClick={() => onUpdateQty(item.key, item.qty + 1)}
                        >
                          +
                        </Button>
                      </div>
                    </div>
                  </div>

                  <Button
                    variant="outline-danger"
                    size="sm"
                    onClick={() => onRemove(item.key)}
                  >
                    âœ•
                  </Button>
                </div>
              </ListGroup.Item>
            ))}
          </ListGroup>
        )}

        <div className="d-flex justify-content-between align-items-center mb-2">
          <span className="fw-bold">Total:</span>
          <span className="fw-bold">
            ${total.toLocaleString('es-CL')}
          </span>
        </div>

        <div className="d-grid gap-2 mt-3">
          <Button
            variant="outline-success"
            disabled={!hasItems}
            onClick={() =>
              window.scrollTo({ top: 0, behavior: 'smooth' })
            }
          >
            ðŸ›’ Continuar comprando
          </Button>

          <Button
            variant="success"
            disabled={!hasItems}
            onClick={onCheckout}
          >
            âœ… Finalizar compra
          </Button>

          <Button
            variant="secondary"
            size="sm"
            onClick={onClear}
            disabled={!hasItems}
          >
            Vaciar carrito
          </Button>
        </div>
      </Card.Body>
    </Card>
  );
}
