// src/components/ProductList.jsx
import { useState, useEffect } from 'react';
import { Row, Col, Alert, Spinner } from 'react-bootstrap';
import { productosAPI } from '../services/api';
import ProductCard from './ProductCard';

export default function ProductList({ onAddToCart }) {
  const [productos, setProductos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    loadProductos();
  }, []);

  const loadProductos = async () => {
    try {
      setLoading(true);
      setError('');
      const data = await productosAPI.getAll();
      setProductos(data);
    } catch (err) {
      setError('Error al cargar productos desde el backend. Verifica que el backend esté corriendo en http://54.83.138.68:8080');
      console.error('Error al conectar con backend:', err);
      // No usar fallback local - forzar uso del backend
      setProductos([]);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="text-center p-5">
        <Spinner animation="border" role="status">
          <span className="visually-hidden">Cargando productos...</span>
        </Spinner>
      </div>
    );
  }

  if (error) {
    return (
      <div>
        <Alert variant="warning">{error}</Alert>
        {productos.length > 0 && (
          <Alert variant="info">
            Mostrando productos desde catálogo local (modo offline)
          </Alert>
        )}
      </div>
    );
  }

  return (
    <div>
      <h2 className="mb-4">Productos Huerto Hogar</h2>
      {productos.length === 0 ? (
        <Alert variant="info">No hay productos disponibles.</Alert>
      ) : (
        <Row>
          {productos.map((producto) => (
            <Col md={4} lg={3} key={producto.id || producto.productoId} className="mb-4">
              <ProductCard
                product={producto}
                onAdd={() => onAddToCart(producto)}
              />
            </Col>
          ))}
        </Row>
      )}
    </div>
  );
}
