// src/components/ShoppingCart.jsx
import { Card, ListGroup, Button } from 'react-bootstrap';
import { CATALOG } from '../data/catalog';

export default function ShoppingCart({ cart, onRemove, onClear, onUpdateQty }) {
  // Enriquecemos el carrito con info del catÃ¡logo
  const detailedItems = cart
    .map((item) => {
      const product = CATALOG.find((p) => p.id === item.id);
      if (!product) return null;
      return {
        ...product,
        qty: item.qty,
        subtotal: product.price * item.qty,
      };
    })
    .filter(Boolean);

  const total = detailedItems.reduce((sum, item) => sum + item.subtotal, 0);
  const hasItems = detailedItems.length > 0;

  return (
    <Card className="shadow-sm position-sticky" style={{ top: '1rem' }}>
      <Card.Body>
        <Card.Title>ðŸ›’ Carrito</Card.Title>

        {!hasItems && (
          <p className="text-muted mb-3">Tu carrito estÃ¡ vacÃ­o.</p>
        )}

        {hasItems && (
          <ListGroup className="mb-3">
            {detailedItems.map((item) => (
              <ListGroup.Item key={item.id}>
                <div className="d-flex justify-content-between align-items-start">
                  <div>
                    <strong>{item.name}</strong>
                    <div className="small text-muted">
                      Subtotal: ${item.subtotal.toLocaleString('es-CL')}
                    </div>

                    {/* Controles de cantidad */}
                    <div className="d-flex align-items-center gap-2 mt-2">
                      <Button
                        variant="outline-secondary"
                        size="sm"
                        onClick={() => onUpdateQty(item.id, item.qty - 1)}
                      >
                        âˆ’
                      </Button>
                      <span>{item.qty}</span>
                      <Button
                        variant="outline-secondary"
                        size="sm"
                        onClick={() => onUpdateQty(item.id, item.qty + 1)}
                      >
                        +
                      </Button>
                    </div>
                  </div>

                  <Button
                    variant="outline-danger"
                    size="sm"
                    onClick={() => onRemove(item.id)}
                  >
                    âœ•
                  </Button>
                </div>
              </ListGroup.Item>
            ))}
          </ListGroup>
        )}

        <div className="d-flex justify-content-between align-items-center mb-2">
          <span className="fw-bold">Total:</span>
          <span className="fw-bold">
            ${total.toLocaleString('es-CL')}
          </span>
        </div>

        <Button
          variant="secondary"
          size="sm"
          className="w-100"
          onClick={onClear}
          disabled={!hasItems}
        >
          Vaciar carrito
        </Button>
      </Card.Body>
    </Card>
  );
}
